import React, { useState } from "react";
import "./styles.css";

function App() {
  const [elements, setElements] = useState([]);
  const [selectedElement, setSelectedElement] = useState(null);
  const [history, setHistory] = useState([]); // This stores the history of added/deleted elements

  const addTextElement = () => {
    const newElement = {
      id: Date.now(),
      text: "Double-click to edit",
      x: 100,
      y: 100,
      fontSize: 16,
      fontStyle: "normal",
      fontWeight: "normal",
      fontFamily: "Arial",
      width: 200,
      height: 30,
    };
    
    // Add to the elements state
    setElements([...elements, newElement]);
    
    // Save the element addition in the history
    setHistory([
      ...history,
      { action: "add", element: newElement },
    ]);
  };

  const deleteTextElement = (id) => {
    const elementToDelete = elements.find(el => el.id === id);

    // Remove the element from the state
    setElements(elements.filter(el => el.id !== id));
    
    // Save the deletion in the history
    setHistory([
      ...history,
      { action: "delete", element: elementToDelete },
    ]);
  };

  const handleTextChange = (id, e) => {
    const textElement = e.target;
    const newText = textElement.innerText;

    const selection = window.getSelection();
    const range = selection.getRangeAt(0);
    const cursorOffset = range.startOffset;

    setElements((prevElements) =>
      prevElements.map((el) => {
        if (el.id === id) {
          return {
            ...el,
            text: newText,
          };
        }
        return el;
      })
    );

    window.requestAnimationFrame(() => {
      const textNode = textElement.firstChild;
      if (textNode) {
        const newRange = document.createRange();
        newRange.setStart(textNode, Math.min(cursorOffset, textNode.length));
        newRange.setEnd(textNode, Math.min(cursorOffset, textNode.length));
        selection.removeAllRanges();
        selection.addRange(newRange);
      }
    });
  };

  const undo = () => {
    const lastAction = history[history.length - 1];
    if (!lastAction) return; // No action to undo

    if (lastAction.action === "add") {
      // Undo add: remove the last added element
      setElements(elements.filter(el => el.id !== lastAction.element.id));
    } else if (lastAction.action === "delete") {
      // Undo delete: restore the last deleted element
      setElements([...elements, lastAction.element]);
    }

    // Remove the last action from history
    setHistory(history.slice(0, -1));
  };

  const redo = () => {
    const lastAction = history[history.length - 1];
    if (!lastAction) return; // No action to redo

    if (lastAction.action === "add") {
      // Redo add: re-add the last added element
      setElements([...elements, lastAction.element]);
    } else if (lastAction.action === "delete") {
      // Redo delete: remove the last deleted element
      setElements(elements.filter(el => el.id !== lastAction.element.id));
    }

    // Remove the last action from history
    setHistory(history.slice(0, -1));
  };

  return (
    <div className="editor">
      <div className="toolbar">
        <button onClick={addTextElement}>Add Text</button>
        <button onClick={undo} disabled={history.length === 0}>Undo</button>
        <button onClick={redo} disabled={history.length === 0}>Redo</button>
      </div>
      <div className="canvas">
        {elements.map((el) => (
          <div
            key={el.id}
            className={`text-element ${
              selectedElement === el.id ? "editing" : ""
            }`}
            style={{
              position: "absolute",
              left: el.x,
              top: el.y,
              fontSize: el.fontSize,
              fontStyle: el.fontStyle,
              fontWeight: el.fontWeight,
              fontFamily: el.fontFamily,
              width: `${el.width}px`,
              height: `${el.height}px`,
              wordWrap: "break-word",
              overflow: "auto",
            }}
            draggable
            onDoubleClick={() => setSelectedElement(el.id)}
            onBlur={() => setSelectedElement(null)}
            contentEditable={selectedElement === el.id}
            onInput={(e) => handleTextChange(el.id, e)}
          >
            {el.text}
            <button onClick={() => deleteTextElement(el.id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
