import React, { useState, useRef } from "react";
import "./styles.css";

function App() {
  const [elements, setElements] = useState([]);
  const [selectedElement, setSelectedElement] = useState(null);
  const historyRef = useRef([[]]);
  const historyIndex = useRef(0);
  const canvasRef = useRef(null);

  // Add new text element
  const addTextElement = () => {
    const newElement = {
      id: Date.now(),
      text: "Double-click to edit",
      x: 100,
      y: 100,
      fontSize: 16,
      fontStyle: "normal",
      fontWeight: "normal",
      fontFamily: "Arial",
      width: 200, // Fixed width
      height: 30, // Fixed height
    };
    updateHistory([...elements, newElement]);
  };

  // Update element properties
  const updateElement = (id, newProps) => {
    const updatedElements = elements.map((el) =>
      el.id === id ? { ...el, ...newProps } : el
    );
    updateHistory(updatedElements);
  };

  // Handle dragging and prevent moving outside canvas bounds
  const handleDrag = (id, e) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    let x = e.clientX - rect.left - 100; // Center the element
    let y = e.clientY - rect.top - 15; // Center the element

    // Ensure the text stays within the bounds of the canvas
    x = Math.max(0, Math.min(x, rect.width - 200)); // 200 is the width of the text element
    y = Math.max(0, Math.min(y, rect.height - 30)); // 30 is the height of the text element

    updateElement(id, { x, y });
  };

  // Handle text change while editing
  const handleTextChange = (id, e) => {
    updateElement(id, { text: e.target.innerText });
  };

  // Undo functionality
  const undo = () => {
    if (historyIndex.current > 0) {
      historyIndex.current -= 1;
      setElements(historyRef.current[historyIndex.current]);
    }
  };

  // Redo functionality
  const redo = () => {
    if (historyIndex.current < historyRef.current.length - 1) {
      historyIndex.current += 1;
      setElements(historyRef.current[historyIndex.current]);
    }
  };

  // Update history for undo/redo
  const updateHistory = (newElements) => {
    historyRef.current = [
      ...historyRef.current.slice(0, historyIndex.current + 1),
      newElements,
    ];
    historyIndex.current += 1;
    setElements(newElements);
  };

  return (
    <div className="editor">
      <div className="toolbar">
        <button onClick={addTextElement}>Add Text</button>
        <button onClick={undo}>Undo</button>
        <button onClick={redo}>Redo</button>
        {selectedElement && (
          <>
            <label>
              Font Size:
              <input
                type="number"
                value={
                  elements.find((el) => el.id === selectedElement)?.fontSize || 16
                }
                onChange={(e) =>
                  updateElement(selectedElement, {
                    fontSize: parseInt(e.target.value),
                  })
                }
              />
            </label>
            <label>
              Font Style:
              <select
                value={
                  elements.find((el) => el.id === selectedElement)?.fontStyle ||
                  "normal"
                }
                onChange={(e) =>
                  updateElement(selectedElement, { fontStyle: e.target.value })
                }
              >
                <option value="normal">Normal</option>
                <option value="italic">Italic</option>
              </select>
            </label>
            <label>
              Font Weight:
              <select
                value={
                  elements.find((el) => el.id === selectedElement)?.fontWeight ||
                  "normal"
                }
                onChange={(e) =>
                  updateElement(selectedElement, { fontWeight: e.target.value })
                }
              >
                <option value="normal">Normal</option>
                <option value="bold">Bold</option>
              </select>
            </label>
            <label>
              Font Family:
              <select
                value={
                  elements.find((el) => el.id === selectedElement)?.fontFamily ||
                  "Arial"
                }
                onChange={(e) =>
                  updateElement(selectedElement, { fontFamily: e.target.value })
                }
              >
                <option value="Arial">Arial</option>
                <option value="Times New Roman">Times New Roman</option>
                <option value="Georgia">Georgia</option>
                <option value="Courier New">Courier New</option>
                <option value="Verdana">Verdana</option>
                <option value="Tahoma">Tahoma</option>
                <option value="Comic Sans MS">Comic Sans MS</option>
                <option value="Cursive">Cursive</option>
                <option value="Monospace">Monospace</option>
              </select>
            </label>
          </>
        )}
      </div>
      <div className="canvas" ref={canvasRef}>
        {elements.map((el) => (
          <div
            key={el.id}
            className={`text-element ${
              selectedElement === el.id ? "editing" : ""
            }`}
            style={{
              position: "absolute",
              left: el.x,
              top: el.y,
              fontSize: el.fontSize,
              fontStyle: el.fontStyle,
              fontWeight: el.fontWeight,
              fontFamily: el.fontFamily,
              width: `${el.width}px`, // Fixed width
              height: `${el.height}px`, // Fixed height
              whiteSpace: "nowrap", // Prevent text from wrapping
              overflow: "hidden", // Prevent overflowing content
            }}
            draggable
            onDrag={(e) => handleDrag(el.id, e)}
            onDoubleClick={() => setSelectedElement(el.id)}
            onBlur={() => setSelectedElement(null)}
            contentEditable={selectedElement === el.id}
            onInput={(e) => handleTextChange(el.id, e)}
          >
            {el.text}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
